export const utilService = {
  getUnreadMsgCount,
  markAllAsRead,
};

function getUnreadMsgCount(msgs,user) {
  var count = 0;
  msgs.forEach((msg) => {
    if (!msg.isRead) count++;
  });
  return count;
}

