import React, { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux'
import ChatList from './ChatList'
import Search from './Search'
import SideBarHeader from './SideBarHeader'
import { logout } from '../../store/user.action'
import { socketService, SOCKET_ON_ADDMSG } from '../../services/socket.service'
import AddChatModal from '../AddChatModal'



export default function SideBar() {
    const dispatch = useDispatch()
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [chats, setChats] = useState([])
    const onLogout = () => {
        dispatch(logout())
    }
    useEffect(() => {
        setSockets();
        return () => {
            turnOffSockets();
        }
    }, [])
    const setSockets = () => {
        socketService.on(SOCKET_ON_ADDMSG, addMsgToChat)
    }
    const turnOffSockets = () => {
        socketService.off(SOCKET_ON_ADDMSG);
    }
    const addMsgToChat = (msg) => {
        const sender = msg.from;
        console.log({ msg })
        chats.map(chat => {
            if (chat.user._id === sender._id) chat.msgs.push(msg);
            return chat;
        })
    }
    const toggleModal = () => {
        setIsModalOpen(!isModalOpen)
    }
    const addChat = (chat) => {
        console.log({ chat })
        chats.push(chat)
    }
    return (
        <section className="side-bar flex column">
            <SideBarHeader openModal={toggleModal} />
            <Search />
            <ChatList chats={chats} />
            <button className="logout-btn" onClick={onLogout}>Logout</button>
            {isModalOpen && <AddChatModal toggleModal={toggleModal} addChat={addChat} />}
        </section>
    )
}
