import React, { useState } from 'react'
import EmojiEmotionsOutlinedIcon from '@mui/icons-material/EmojiEmotionsOutlined';
import AttachFileOutlinedIcon from '@mui/icons-material/AttachFileOutlined';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
export default function ChatMsgSend({ sendMsg }) {
    const [msg, setMsg] = useState('');
    const handleChange = ({ target }) => {
        setMsg(target.value)
    }
    const onSendMsg = () => {
        sendMsg(msg);
    }
    return (
        <div className="chat-send-container flex">
            <input type="text" className="msg-send-txt" value={msg} onChange={handleChange} placeholder="Type your message here..." />
            <div className="chat-msg-btns flex">
                <button className="btn-1"><EmojiEmotionsOutlinedIcon /></button>
                <button className="btn-2"><AttachFileOutlinedIcon /></button>
                <button className="btn-3" onClick={onSendMsg}><SendOutlinedIcon className="send-icon" /></button>
            </div>
        </div>
    )
}
