import React, { useState } from 'react'
import EmojiEmotionsOutlinedIcon from '@mui/icons-material/EmojiEmotionsOutlined';
import AttachFileOutlinedIcon from '@mui/icons-material/AttachFileOutlined';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import { socketService, SOCKET_EMIT_TYPING } from '../../services/socket.service';
export default function ChatMsgSend({ sendMsg, chat }) {
    const [msg, setMsg] = useState('');

    const handleChange = ({ target }) => {
        setMsg(target.value)
        socketService.emit(SOCKET_EMIT_TYPING, chat)
    }

    const onSendMsg = () => {
        sendMsg(msg);
        setMsg('');
    }

    const handlePress = ev => {
        if (ev.key === "Enter")
            onSendMsg();
    }
    return (
        <div className="chat-send-container flex">
            <input type="text" className="msg-send-txt" value={msg} onChange={handleChange} placeholder="Type your message here..." onKeyPress={handlePress} />
            <div className="chat-msg-btns flex">
                <button className="btn-1"><EmojiEmotionsOutlinedIcon />
                <div className=""></div>
                </button>
                <button className="btn-2"><AttachFileOutlinedIcon /></button>
                <button className="btn-3" onClick={onSendMsg}><SendOutlinedIcon className="send-icon" /></button>
            </div>
        </div>
    )
}
