import React, { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux'
import ChatList from './ChatList'
import Search from './Search'
import SideBarHeader from './SideBarHeader'
import { logout } from '../../store/user.action'
import { socketService, SOCKET_ON_ADDMSG } from '../../services/socket.service'
import AddChatModal from '../AddChatModal'

let chats = [
    {
        user: { _id: '622c7c42309e3fc3e9311c66', username: "ben123", phone: "0543070819" },
        msgs: [{ from: '622c7c54309e3fc3e9311c67', to: '622c7c42309e3fc3e9311c66', txt: 'hello test', isRead: false, sentAt: 1647250643258 }, { from: '622c7c54309e3fc3e9311c67', to: '622c7c42309e3fc3e9311c66', txt: 'hello test', isRead: false, sentAt: 1647250663905 }]
    },
    {
        user: { _id: '622c7c54309e3fc3e9311c67', username: "noam123", phone: "0543928819" },
        msgs: [{ from: { _id: '622c7c54309e3fc3e9311c67', username: "noam123", phone: "0543928819" }, to: '622c7c42309e3fc3e9311c66', txt: 'hello test', isRead: false, sentAt: 1647250643258 }, { from: { _id: '622c7c54309e3fc3e9311c67', username: "noam123", phone: "0543928819" }, to: '622c7c42309e3fc3e9311c66', txt: 'hello test', isRead: false, sentAt: 1647250663905 }]
    }
]


export default function SideBar() {
    const dispatch = useDispatch()
    const [isModalOpen, setIsModalOpen] = useState(false);
    const onLogout = () => {
        dispatch(logout())
    }
    useEffect(() => {
        setSockets();
        return () => {
            turnOffSockets();
        }
    }, [])
    const setSockets = () => {
        socketService.on(SOCKET_ON_ADDMSG, addMsgToChat)
    }
    const turnOffSockets = () => {
        socketService.off(SOCKET_ON_ADDMSG);
    }
    const addMsgToChat = (msg) => {
        const sender = msg.from;
        console.log({ msg })
        chats.map(chat => {
            if (chat.user._id === sender._id) chat.msgs.push(msg);
            return chat;
        })
    }
    const toggleModal = () => {
        setIsModalOpen(!isModalOpen)
    }
    return (
        <section className="side-bar flex column">
            <SideBarHeader openModal={toggleModal} />
            <Search />
            <ChatList chats={chats} />
            <button className="logout-btn" onClick={onLogout}>Logout</button>
            {isModalOpen && <AddChatModal toggleModal={toggleModal} />}
        </section>
    )
}
