import React from 'react'
import { useSelector } from 'react-redux'
import UserProfileImage from "../profile/UserProfileImage"
export default function ChatBubble({ msg }) {
    const user = useSelector(({ userModule }) => userModule.user)
    const className = (msg.from._id === user._id) ? "my-msg" : "";
    const profile = msg.from._id === user._id ? user : msg.user;

    const formatTime = (timestamp) => {
        const date = new Date(timestamp);
        let hour = date.getHours();
        if (hour < 10) hour = '0' + hour;
        let minutes = date.getMinutes();
        if (minutes < 10) minutes = '0' + minutes;
        return hour + ':' + minutes;
    }

    const time = formatTime(msg.sentAt);

    return (
        <div className="msg-container flex column">
            <div className={`chat-bubble ${className}`}>
                <p>{msg.txt}</p>
            </div>
            <UserProfileImage user={profile} withDot={false} />
        </div>

    )
}
