export const utilService = {
  getUnreadMsgCount,
  markAllAsRead,
};

function getUnreadMsgCount(msgs) {
  var count = 0;
  msgs.forEach((msg) => {
    if (!msg.isRead) count++;
  });
  return count;
}

function markAllAsRead(msgs) {
  return msgs.map((msg) => ({ ...msg, isRead: true }));
}
