export const utilService = {
  getUnreadMsgCount,
};

function getUnreadMsgCount(msgs) {
  var count = 0;
  msgs.forEach((msg) => {
    if (!msg.isRead) count++;
  });
  return count;
}

function markAllAsRead(msgs) {
  msgs.forEach((msg) => (msg.isRead = true));
}
