import React from 'react'
import { useDispatch } from 'react-redux'
import ChatList from './ChatList'
import Search from './Search'
import SideBarHeader from './SideBarHeader'
import { logout } from '../../store/user.action'
const chats = [
    {
        user: { _id: '622c7c42309e3fc3e9311c66', username: "ben123", phone: "0543070819" },
        msgs: [{ from: '622c7c54309e3fc3e9311c67', to: '622c7c42309e3fc3e9311c66', txt: 'hello test', isRead: false, sentAt: 1647250643258 }, { from: '622c7c54309e3fc3e9311c67', to: '622c7c42309e3fc3e9311c66', txt: 'hello test', isRead: false, sentAt: 1647250663905 }, { from: '622c7c54309e3fc3e9311c67', to: '622c7c42309e3fc3e9311c66', txt: 'hello test', isRead: false, sentAt: 1647250672483 }]
    },
]


export default function SideBar() {
    const dispatch = useDispatch()
    const onLogout = () => {
        dispatch(logout())
    }
    return (
        <section className="side-bar flex column">
            <SideBarHeader />
            <Search />
            <ChatList chats={chats} />
            <button className="logout-btn" onClick={onLogout}>Logout</button>
        </section>
    )
}
