import React, { useState, useEffect, useContext } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import ChatHeader from './ChatHeader'
import ChatList from './ChatList'
import ChatMsgSend from './ChatMsgSend'

import { socketService, SOCKET_EMIT_CONNECTED, SOCKET_EMIT_NEWMSG, SOCKET_ON_ADDMSG } from '../../services/socket.service'
import NoMsg from './NoMsg'
import { ChatContext } from '../../context/chatContext'
import { updateUser } from '../../store/user.action'


export default function ChatContent({ chat }) {
    const user = useSelector(({ userModule }) => userModule.user);
    const { currChat } = useContext(ChatContext);
    const dispatch = useDispatch()
    useEffect(() => {
        setSockets()
        return () => {
            setSocketsOff();
        }
    }, [])
    const setSockets = () => {
        socketService.emit(SOCKET_EMIT_CONNECTED, user._id)
    }
    const setSocketsOff = () => {
        socketService.off(SOCKET_EMIT_CONNECTED);
    }
    const addMsg = (txt) => {
        const tempUser = { ...user };
        const miniUser = { ...user };
        delete miniUser.chats;
        const idx = tempUser.chats.findIndex(chat => chat.user._id === currChat.user._id)
        const msg = { txt, from: miniUser, to: chat.user._id, sentAt: Date.now() };
        tempUser.chats[idx].msgs.push(msg);
        socketService.emit(SOCKET_EMIT_NEWMSG, msg);
        dispatch(updateUser(tempUser));
    }
    return (
        <section className="chat flex column">
            {(!chat || !chat.msgs) ? <NoMsg /> :
                <>
                    <ChatHeader user={chat.user} />
                    <ChatList msgs={chat.msgs} />
                    <ChatMsgSend sendMsg={addMsg} />
                </>
            }
        </section>
    )
}
