import React, { useContext, useState,useEffect } from 'react'
import { ChatContext } from '../../context/chatContext'
import { utilService } from '../../services/util.service';
import UserProfileImage from '../profile/UserProfileImage'

export default function ChatPreview({ chat }) {
    const { currChat, setChat } = useContext(ChatContext);
    const user = chat.user;
    const [unreadCount, setUnreadCount] = useState(utilService.getUnreadMsgCount(chat.msgs))

    useEffect(() => {
        setSockets();
        return () => {
            clearSockets();
        }
    }, [])

    const onSetCurrentChat = () => {
        if (chat.user._id === currChat?.user._id) return;
        if (unreadCount !== 0) {
            setUnreadCount(0);
        }
        setChat(chat);
    }


    const sub = (chat.msgs[0]) ? chat.msgs[chat.msgs.length - 1].txt : "New Chat"

    return (
        <div onClick={onSetCurrentChat} className="chat-preview flex">
            <UserProfileImage user={user} />
            <div className="chat-preview-content">
                <h1>{user.username}</h1>
                <p>{sub}</p>
            </div>
            {(unreadCount > 0) && <div className="unread-count">
                <h1>{unreadCount}</h1>
            </div>}
        </div>
    )
}
