import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux'

import ChatHeader from './ChatHeader'
import ChatList from './ChatList'
import ChatMsgSend from './ChatMsgSend'

import { socketService, SOCKET_EMIT_CONNECTED, SOCKET_EMIT_NEWMSG, SOCKET_ON_ADDMSG } from '../../services/socket.service'


export default function ChatContent({}) {
    const [msgs, setMsgs] = useState([]);
    const user = useSelector(({ userModule }) => userModule.user)
    useEffect(() => {
        setSockets()
        return () => {
            setSocketsOff();
        }
    }, [])
    const setSockets = () => {
        socketService.emit(SOCKET_EMIT_CONNECTED, { id: user._id })
    }
    const setSocketsOff = () => {

    }
    const addMsg = (txt) => {
        const msg = { txt };
        socketService.emit(SOCKET_EMIT_NEWMSG, msg);
        setMsgs([...msgs, msg]);
    }
    return (
        <section className="chat flex column">
            <ChatHeader />
            <ChatList msgs={msgs} />
            <ChatMsgSend sendMsg={addMsg} />
        </section>
    )
}
