  getUnreadMsgCount,
  markAllAsRead,
};

function getUnreadMsgCount(msgs, userId) {
  var count = 0;
  msgs.forEach((msg) => {
    if (!msg.viewers || !msg.viewers.length) {
      count++;
    } else {
      const ans = msg.viewers.find((viewer) => viewer._id === userId);
