import ChatPreview from './ChatPreview'
import { socketService, SOCKET_ON_ADDMSG } from '../../services/socket.service'
export default function ChatList({ chats }) {
    useEffect(() => {
        setSockets();
        return () => {
            turnOffSockets();
        }
    }, [])
    const setSockets = () => {
        socketService.on(SOCKET_ON_ADDMSG, msg => {
            console.log({ msg });
        })
    }
    const turnOffSockets = () => {
     socketService.off()
    }
    return (
        <section className="chat-list-container">
            {(chats) && chats.map(chat => <ChatPreview chat={chat} key={chat.user._id} />)}
        </section >
    )
}
