import React, { useContext, useState, useEffect } from 'react'
import { ChatContext } from '../../context/chatContext'
import { socketService, SOCKET_ON_TYPING } from '../../services/socket.service';
import { utilService } from '../../services/util.service';
import UserProfileImage from '../profile/UserProfileImage'

export default function ChatPreview({ chat }) {
    const { currChat, setChat } = useContext(ChatContext);
    const user = chat.user;
    const [unreadCount, setUnreadCount] = useState(utilService.getUnreadMsgCount(chat.msgs))
    const [isTyping, setIsTyping] = useState(false)
    const [gTimeout, setGTimeout] = useState(null)

    useEffect(() => {
        setSockets();
        return () => {
            clearSockets();
            setGTimeout(null)
        }
    }, [])

    const setSockets = () => {
        socketService.on(SOCKET_ON_TYPING, (chatId) => {
            if (chat._id === chatId) {
                setIsTyping(true);
                setGTimeout(null)
                setGTimeout(setTimeout(() => {
                    setIsTyping(false)
                }, 3000))
            }
        })
    }

    const clearSockets = () => {
        socketService.off(SOCKET_ON_TYPING);
    }

    const onSetCurrentChat = () => {
        if (chat.user._id === currChat?.user._id) return;
        if (unreadCount !== 0) {
            const updatedMsg=utilService.markAllAsRead()
        }
        setChat(chat);
    }


    let sub = (chat.msgs[0]) ? chat.msgs[chat.msgs.length - 1].txt : "New Chat"
    sub = (isTyping) ? "typing..." : sub;
    return (
        <div onClick={onSetCurrentChat} className="chat-preview flex">
            <UserProfileImage user={user} />
            <div className="chat-preview-content">
                <h1>{user.username}</h1>
                <p>{sub}</p>
            </div>
            {(unreadCount > 0) && <div className="unread-count">
                <h1>{unreadCount}</h1>
            </div>}
        </div>
    )
}
