import React, { useEffect, useContext } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import ChatHeader from './ChatHeader'
import ChatList from './ChatList'
import ChatMsgSend from './ChatMsgSend'

import { socketService, SOCKET_EMIT_CONNECTED, SOCKET_EMIT_NEWMSG, SOCKET_EMIT_UPDATED_CHAT, SOCKET_ON_ADDCHAT, SOCKET_ON_ADDMSG, SOCKET_ON_UPDATE_CHAT } from '../../services/socket.service'
import NoMsg from './NoMsg'
import { ChatContext } from '../../context/chatContext'
import { socketAddChat, updateChat, socketUpdateChat } from '../../store/chat.action'
import { utilService } from '../../services/util.service'

export default function ChatContent({ chat }) {

    const { user } = useSelector(({ userModule }) => userModule);
    const { chats } = useSelector(({ chatModule }) => chatModule);
    const dispatch = useDispatch()

    const { currChat, setChat } = useContext(ChatContext);

    useEffect(() => {
        setSockets()
        return () => {
            setSocketsOff();
        }
    }, [])

    useEffect(() => {
        socketService.on(SOCKET_ON_ADDMSG, onRecivedMsg)
        socketService.on(SOCKET_ON_UPDATE_CHAT,onChatUpdated)
        return () => {
            socketService.off(SOCKET_ON_ADDMSG)
        }
    }, [chats])

    const setSockets = () => {
        socketService.emit(SOCKET_EMIT_CONNECTED, user._id)
        socketService.on(SOCKET_ON_ADDCHAT, onAddChat)
    }

    const setSocketsOff = () => {
        socketService.off(SOCKET_EMIT_CONNECTED);
    }

    const addMsg = (txt) => {
        const msg = { txt, from: user, sentAt: Date.now(), viewers: [user._id] };
        const chat = { ...currChat };
        chat.msgs.push(msg);
        setChat(chat);
        const updatedChat = { ...chat };
        updatedChat.users = [user, chat.user];
        delete updatedChat.user;
        const reciverId = (chat.user._id)
        socketService.emit(SOCKET_EMIT_NEWMSG, { updatedChat, reciverId });
        dispatch(updateChat(updatedChat));
    }

    const onRecivedMsg = (chat) => {
        const onUpdatedChat = chats.find(cChat => cChat._id === chat._id);
        const currMsgs = onUpdatedChat.msgs;
        const msgs = chat.msgs.map((msg, idx) => {
            if (currMsgs.length > idx) {
                if (currMsgs[idx].viewers.includes(user._id) && !(msg.viewers.includes(user._id))) {
                    const updatedMsg = { ...msg };
                    updatedMsg.viewers.push(user._id);
                    return updatedMsg;
                }
                else {
                    return msg
                }
            }
            else {
                return msg;
            }
        })
        const updatedChat = { ...chat, msgs };
        dispatch(socketUpdateChat(updatedChat))
        if (updatedChat._id === chat._id) {
            updatedChat.msgs = utilService.markAllAsRead(msgs, user._id)
            setChat(updatedChat);
            socketService.emit(SOCKET_EMIT_UPDATED_CHAT, updatedChat);
            updatedChat.users = [user, updatedChat.user]
            delete updatedChat.user
            dispatch(updateChat(updatedChat));
        }
    }

    const onAddChat = (chat) => {
        dispatch(socketAddChat(chat))
    }

    return (
        <section className="chat flex column">
            {(!chat || !chat.msgs) ? <NoMsg /> :
                <>
                    <ChatHeader user={chat.user} />
                    <ChatList msgs={chat.msgs} chatId={chat._id} />
                    <ChatMsgSend sendMsg={addMsg} chat={chat} />
                </>
            }
        </section>
    )
}
